roulette.body:
// ruby
var item = new RPGObject();
item.mod(Hack.assets.ruby);
item.locate(7, 5, 'map1');
const start = () => {
	item.timer = setInterval(() => {
		const randomMod = random([
			Hack.assets.ruby,
			Hack.assets.skull,
			Hack.assets.poo
		]);
		item.mod(randomMod);
		item._mod = randomMod; // 判定のために入れておく
	}, 100);
};
const stop = () => {
	clearInterval(item.timer);
	if (item._mod === Hack.assets.ruby) {
		Hack.score += 1; // rubyなら、SCORE + 1
	}
	if (item._mod === Hack.assets.skull) {
		Hack.score -= 1; // skullなら、SCORE - 1
	}
};
item.flag = true;
item.onattacked = function() {
	if (item.flag) {
		// ルーレット開始！
		start();
		item.flag = false;
	} else {
		// ルーレット停止！
		stop();
		item.flag = true;
	}
};

