	item2.velocityX += random(-0.99, 1);
	item2.velocityY += random(-0.99, 1);
	item2.scale(random(0.99, 1.5));
	item2.force(-fx * random(0, 0.199), -fy * random(0, 0.199));
	item2.destroy(20);
	item2.onふれはじめた = (event) => {
		if (event.hit !== item1) {
			Hack.Attack(event.mapX, event.mapY, item1.atk);
		}
	};
};

