// ルーレット
const item1 = new RPGObject();
item1.mod(_rルビー);
item1.locate(7, 5, 'map1');
const start = () => {
	item1.timer = setInterval(() => {
		const randomMod = random([
			_rルビー,
			_dドクロ,
			_uうんこ
		]);
		item1.mod(randomMod);
		item1._mod = randomMod; // 判定のために入れておく
	}, 100);
};
const stop = () => {
	clearInterval(item1.timer);
	if (item1._mod === _rルビー) {
		Hack.score += 1; // rubyなら、SCORE + 1
	}
	if (item1._mod === _dドクロ) {
		Hack.score -= 1; // skullなら、SCORE - 1
	}
};
item1.flag = true;
item1.onこうげきされた = () => {
	if (item1.flag) {
		// ルーレット開始！
		start();
		item1.flag = false;
	} else {
		// ルーレット停止！
		stop();
		item1.flag = true;
	}
};

