// ↓ビームの本体. この部分はひとつだけあれば良い
function beamSkill(self) {
	const beam = new RPGObject();
	beam.mod(_bビーム);
	beam.onふれはじめた = (event) => {
		if (event.hit !== self) {
			Hack.Attack(event.mapX, event.mapY, 1);
			beam.destroy();
		}
	};
	self.shoot(beam, self.forward, 10);
}
