// ルーレット
const item2 = new RPGObject();
item2.mod(Hack.assets.ruby);
item2.locate(7, 5, 'map1');
const start = () => {
	item2.timer = setInterval(() => {
		const randomMod = random([
			Hack.assets.ruby,
			Hack.assets.skull,
			Hack.assets.poo
		]);
		item2.mod(randomMod);
		item2._mod = randomMod; // 判定のために入れておく
	}, 100);
};
const stop = () => {
	clearInterval(item2.timer);
	if (item2._mod === Hack.assets.ruby) {
		Hack.score += 1; // rubyなら、SCORE + 1
	}
	if (item2._mod === Hack.assets.skull) {
		Hack.score -= 1; // skullなら、SCORE - 1
	}
};
item2.flag = true;
item2.onattacked = () => {
	if (item2.flag) {
		// ルーレット開始！
		start();
		item2.flag = false;
	} else {
		// ルーレット停止！
		stop();
		item2.flag = true;
	}
};

