beam.body:
Assets.get('player').then((self) => {
	self.onbecomeattack = () => {
		beam(self);
	};
});

function beam(self) {
	Assets.beam().then((beam) => {
		beam.ontriggerenter = (event) => {
			if (event.hit !== self) {
				Hack.Attack(event.mapX, event.mapY, 1);
				beam.destroy();
			}
		};
		self.shoot(beam, self.forward, 10);
	});
}
