enchantjs/monster4.gif.body:
// slime
var item = new RPGObject();
item.mod(Hack.assets.slime);
item.hp = 3;
item.atk = 1;
item.locate(7, 5, 'map1');
item.onbecomeidle = () => {
	item.attack();
};

enchantjs/monster1.gif.body:
// insect
var item = new RPGObject();
item.mod(Hack.assets.insect);
item.hp = 2;
item.atk = 1;
item.locate(7, 5, 'map1');
item.onbecomeidle = () => {
	item.turn();
	item.walk();
};
item.onbecomedead = () => {
	Hack.score += 1;
};

enchantjs/monster5.gif.body:
// ouroboros
var item = new RPGObject();
item.mod(Hack.assets.ouroboros);
item.hp = 10;
item.atk = 1;
item.locate(7, 5, 'map1');
setInterval(() => {
	item.attack();
}, 4000);

enchantjs/bigmonster2.gif.body:
// minotaur
var item = new RPGObject();
item.mod(Hack.assets.minotaur);
item.hp = 10;
item.atk = 1;
item.locate(7, 5, 'map1');
item.scale(2, 2);
item.onbecomeidle = () => {
	item.attack();
};
